SET @r := -1;
INSERT INTO Vehicle (type, status, capacity, operator_id)
SELECT
    CASE MOD(@r := @r + 1, 3)
        WHEN 0 THEN 'AMBULANCE'
        WHEN 1 THEN 'FIRETRUCK'
        ELSE 'POLICE'
        END AS type,
    'AVAILABLE' AS status,
    CASE MOD(@r, 3)
        WHEN 0 THEN 2
        WHEN 1 THEN 8
        ELSE 4
        END AS capacity,
    u.id AS operator_id
FROM `User` u
WHERE u.email LIKE 'operator%@sim.local'
ORDER BY u.id
    LIMIT 100;
